bool inters (vec3 v) {
    return (v.z > 1.0-texture(iChannel1, v.xy/2.0).r/5.);

}
vec4 ray(vec2 rs, vec2 uv) {
    vec3 cuv = vec3(uv-vec2(0.5), 1);
    for (int t = 0; t < 150; t++) {
    	vec3 p = vec3(rs, .5)+cuv*(float(t)/128.0);
        if (inters(p)) return vec4(texture(iChannel0, p.xy/2.0).rgb, 1);
    }
    return vec4(0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 tuv = uv+(vec2(normalize(iMouse))*iResolution.xy/100.0);
	fragColor = ray(tuv, uv);
}
