void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);    

    float distanceFromMouse = (distance(fragCoord.xy, iMouse.xy) / iResolution.y);

    if(distanceFromMouse > 0.15){
    	fragColor.xyz = vec3(0.0, 0.0, 0.0);
    }
    else if(distanceFromMouse >= 0.05){
        vec3 addColor = vec3(0.0, 0.0, 0.0);
        float col = dot(fragColor.xyz, addColor);    
        float blendValue = 1.0 - (exp(0.05 / distanceFromMouse) / 2.7);
        fragColor.xyz =  mix(fragColor.xyz, vec3(col), blendValue);
    }

}
