#define BUFFER_U iChannel0

#define LIGHT vec3(3.0, 2.0, -2.0)
#define DEPTH 0.5

#define USE_BACKGROUND

float unpack(const in vec4 value)
{
    return value.a;
}

float get(in sampler2D tex, in vec2 uv)
{
    return unpack(texture(tex, uv));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = 1.0 / iResolution.xy;

    float p0 = get(BUFFER_U, uv + p * vec2(-1, 0));
    float p1 = get(BUFFER_U, uv + p * vec2( 1, 0));
    float p2 = get(BUFFER_U, uv + p * vec2( 0,-1));
    float p3 = get(BUFFER_U, uv + p * vec2( 0, 1));

    vec3 n = normalize(vec3(2.0 * (p0 - p1), 1.0, 2.0 * (p2 - p3)));

#ifdef USE_BACKGROUND
    vec3 c = texture(iChannel1,uv + n.xz * DEPTH).xyz;
#else
    vec3 c = vec3(dot(n, normalize(LIGHT)));
#endif
    if (texture(iChannel2, uv).a > 0.0)
    	c = texture(iChannel3, uv).xyz;

    fragColor = vec4(c, 1);
}

// v buffer

#define BUFFER_V iChannel0
#define BUFFER_U iChannel1

#define DAMPING 0.99

#define RESETKEY 81.5/256.0

//const float c = 1.0;
const float dt = 0.5;

vec4 pack(const in float value)
{
    return vec4(0, 0, 0, value / 2.0 + 0.5);
}

float unpack(const in vec4 value)
{
    return value.a * 2.0 - 1.0;
}

float get(in sampler2D tex, in vec2 uv)
{
    return unpack(texture(tex, uv));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = 1.0 / iResolution.xy;

    float c = 1.0 - texture(iChannel3, uv).a;

    float f = 0.0;
    f += get(BUFFER_U, uv + p * vec2(-1, 0));
    f += get(BUFFER_U, uv + p * vec2( 1, 0));
    f += get(BUFFER_U, uv + p * vec2( 0,-1));
    f += get(BUFFER_U, uv + p * vec2( 0, 1));
    f -= get(BUFFER_U, uv) * 4.0;
    f *= c * c;

    float v = get(BUFFER_V, uv);
    v += f * dt;
    v *= DAMPING;

    if (iFrame == 0 || iTime < 1.0 || texture(iChannel2, vec2(RESETKEY, 0.5)).x > 0.0)
        v = 0.0;

    fragColor = pack(v);
}

// u buffer

#define BUFFER_V iChannel0
#define BUFFER_U iChannel1

#define RESETKEY 81.5/256.0

const float dt = 0.5;

vec4 pack(const in float value)
{
    return vec4(0, 0, 0, value / 2.0 + 0.5);
}

float unpack(const in vec4 value)
{
    return value.a * 2.0 - 1.0;
}

float get(in sampler2D tex, in vec2 uv)
{
    return unpack(texture(tex, uv));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float u = get(BUFFER_U, uv);
    float v = get(BUFFER_V, uv);

    u += v*dt;

    if (iFrame == 0 || iTime < 1.0 || texture(iChannel2, vec2(RESETKEY, 0.5)).x > 0.0)
        u = 0.0;
    if (distance(fragCoord.xy, iMouse.xy) < 1.0 && iMouse.z > 0.0)
        u = 1.0;

    fragColor = pack(u);
}

#define DRAWKEY 87.5/256.0
#define RESETKEY 81.5/256.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = texture(iChannel0, uv);

    if (iFrame == 0 || iTime < 1.0 || texture(iChannel1, vec2(RESETKEY, 0.5)).x > 0.0)
        color.a = 0.0;
    if (texture(iChannel1, vec2(DRAWKEY, 0.5)).x > 0.0 && distance(fragCoord.xy, iMouse.xy) < 10.0 && iMouse.z > 0.0)
        color.a = 1.0;

    fragColor = vec4(color);
}
