#define step 0.01
#define cutoff 0.05

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 c = texture(iChannel0,uv);
    for (float i=1.0;i>0.;i-=step)
    {
         if (c.g > i && c.r < i-cutoff && c.b < i-cutoff) c=vec4(0.);
    }
    fragColor=c;
}
