//derrived from johnnyparafango's code 
//http://stackoverflow.com/questions/36883952/processing-3-improving-intensive-math-calculation/36893850
const float TWO_PI = 6.283185307179586;

vec2 pt1 = vec2(0.25,0.5);
vec2 pt2 = vec2(0.75,0.5);

float factorA = 0.10;
float factorB = 0.40;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float d1 = distance(pt1,uv);
  	float d2 = distance(pt2,uv);

    factorA = iMouse.x * sin(iTime * 0.01) * 0.000001;
    factorB = iMouse.y * 0.01;

  	float wave1 = (1.0 + (sin(TWO_PI * d1/factorA + iTime))) * 0.5 * exp(-d1/factorB);
  	float wave2 = (1.0 + (sin(TWO_PI * d2/factorA + iTime))) * 0.5 * exp(-d2/factorB);

  	vec3 gray = vec3(wave1 + wave2);

  	fragColor=vec4(gray,1.0);
}
