void mainImage(out vec4 fragColor, in vec2 fragCoord)
{  
    vec4 color = vec4(0.0f);
   	vec2 ndc = (2.0 * fragCoord - iResolution.xy ) / iResolution.y;  
    vec2 mouse = iMouse.xy;
	if (mouse != vec2(0))
        mouse = (2.0 * mouse - iResolution.xy)  / iResolution.y;

    float radius0 = distance(mouse, ndc + vec2(0.5, 0.0));
    float radius1 = distance(mouse, ndc - vec2(0.5, 0.0));

    float intensity0 = 0.5 * Wave(6.0f, radius0, +iTime) + 0.5;   
    float intensity1 = 0.5 * Wave(4.0f, radius1, -iTime) + 0.5;

    color += vec4(intensity0 * exp(-radius0 / M_COLOR), 1.0);
    color += vec4(intensity1 * exp(-radius1 / M_COLOR), 1.0);

    fragColor = color;
}

vec2 mainSound( in int samp, float time )
{    
    float sum = 0.0f;
    sum += Wave(6.0f, 0.0, time);   
    sum += Wave(4.0f, 0.0, time);     

    return vec2(5.0 * sum);
}

#define M_PI        3.1415              
#define M_COLOR     vec3(0.1, 0.0, 0.5)


float Wave(float v, float phi, float time)
{
    return cos(2.0 * M_PI * v * (time - phi));
}
