/**
* Written by Jung, Yucheol <ycjung@postech.ac.kr>
* ycjungSubhuman@github.com
*
* Reference : Robin Green, Spherical Harmonic Lighting - The Grity Details
* Visualizes spherical harmonics
*
* Calculates SH without using pre-computed values
*/

/** 
 * MODIFY THESE VALUES TO SEE DIFFERENT SHs 
 * L > 0, L is int.
 * -L <= M <= L, M is int.
 */
#define L 1
#define M 0

/* MATH UTILITIES *************************************************************/
#define PI 3.14159265358979323846264
#define SQRT_TWO 1.41421356237309504880169

const int factorial_table[12] = int[12](
  1,
  2,
  6,
  24,
  120,
  720,
  5040,
  40320,
  362880,
  3628800,
  39916800,
  479001600);

int factorial (int n) {
  return factorial_table[n-1];
}

/* SH *************************************************************************/

/**
* Calculate associated Legendre polynomial
*
* "Numerical Methods in C: The Art of Scientific Computingâ€,
* Cambridge University Press, 1992, pp 252-254
*/
float P(int l, int m, float x) {
  // Calculate P_m^m first (Because it has a closed form)
  float p_mm=1.0;
  if (m>0) {
    float somx2 = sqrt((1.0-x)*(1.0+x));
    float fact = 1.0;
    for(int i=1; i<=m; i++) {
      p_mm *= (-fact) * somx2;
      fact += 2.0;
    }
  }
  // If l==m, no need for further calculation, return.
  if (l==m) return p_mm;
  // Proceed to P_{m+1}^{m} using P_m^m
  float p_mmp1 = x * (2.0*float(m) + 1.0) * p_mm;
  // Just a micro optimization with early return
  if (l==m+1) return p_mmp1;
  // Calclulate general p_l^{m}
  // Using relation
  //   (l-m)P_l^m = x(2l-1)P_{l-1}^m - (l+m-1)P_{l-2}^m
  float p_ll = 0.0;
  for (int ll=m+2; ll<=l; ll++) {
    p_ll = ( (2.0*float(ll)-1.0)*x*p_mmp1-(float(ll)+float(m)-1.0)*p_mm )
    / float(ll-m);
    p_mm = p_mmp1;
    p_mmp1 = p_ll;
  }
  return p_ll;
}

/** Normalization coefficient for SH */
float K(int l, int m) {
  return sqrt(
    (2.0*float(l)+1.0)*float(factorial(l-m))
    / (4.0*PI*float(factorial(l+m)))
    );
}

/** SH of l, m, theta(zenith), phi(azimuth) */
float SH(int l, int m, float theta, float phi) {
  if (m==0) {
    return K(1, 0)*P(l, m, cos(theta));
  }
  else if (m>0) {
    return SQRT_TWO*K(l,m)*cos(float(m)*phi)*P(l,m,cos(theta));
  }
  else {
    return SQRT_TWO*K(l,-m)*sin(-float(m)*phi)*P(l,-m,cos(theta));
  }
}

/* RAYTRACING *****************************************************************/
// Reference : Annotated Ray Tracing by Polytonic
// (https://www.shadertoy.com/view/4ljGRd)
// Reference : Jamie Wong, Ray marching signed distance functions
// (http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/)
#define RENDER_EPSILON    1e-4
#define RENDER_ITERATION  200
#define RENDER_RAY_RECURSION  1
#define RENDER_ENDDEPTH   50.0
#define RENDER_EXPOSURE   5e-2
#define RENDER_GAMMA      2.2
#define RENDER_INTENSITY  100.0
#define RENDER_AMBIENT (vec3(0.6, 0.8, 1.0) * RENDER_INTENSITY / RENDER_GAMMA)

struct Ray {
  vec3 origin;
  vec3 direction;
};

struct Light {
  vec3 color;
  vec3 direction;
};

struct Intersect {
  float len;
  vec3 normal;
};

const Intersect MISS = Intersect(0.0, vec3(0.0));

/* RAYTRACING-SDF *************************************************************/
float sdf_sh(int l, int m, float scale, vec3 sh_position, vec3 ray_position, vec3 ro) {
  vec3 p = (ray_position - sh_position);
  float pr = length(p);
  float theta = atan(sqrt(p.x*p.x + p.y*p.y)/p.z);
  float phi = PI+atan(p.x, p.y);

  float fr = scale*abs(SH(l, m, theta, phi));

  vec3 delta = ray_position - fr*normalize(p);
  if(dot(delta, p) < 0.0) {
      return -length(delta);
  }
  else {
      return length(delta);
  }
}

/* RAYTRACING-SCENE-RENDER ****************************************************/
#define NUM_SH_BANDS 5
#define SCENE_SH_COUNT (NUM_SH_BANDS*NUM_SH_BANDS)

float sdf_scene(vec3 p, vec3 ro) {
  float sh = sdf_sh(L,M, 5.0, vec3(0.0, 0.0, 0.0), p, ro);
  return sh;
}

vec3 estimate_normal(vec3 p, vec3 ro) {
  return normalize(vec3(
    sdf_scene(vec3(p.x + RENDER_EPSILON, p.y, p.z), ro)
    - sdf_scene(vec3(p.x - RENDER_EPSILON, p.y, p.z), ro),
    sdf_scene(vec3(p.x, p.y + RENDER_EPSILON, p.z), ro)
    - sdf_scene(vec3(p.x, p.y - RENDER_EPSILON, p.z), ro),
    sdf_scene(vec3(p.x, p.y, p.z + RENDER_EPSILON), ro)
    - sdf_scene(vec3(p.x, p.y, p.z - RENDER_EPSILON), ro)
    ));
}

Intersect trace(Ray ray) {
  float depth = 0.0;

  for (int i=0; i<RENDER_ITERATION; i++) {
    float dist = sdf_scene(ray.origin + depth*ray.direction, ray.origin);

    if (dist < RENDER_EPSILON) {
      break;
    }
    depth += dist;

    if (depth >= RENDER_ENDDEPTH) {
      return MISS;
      break;
    }
  }
  return Intersect(depth, estimate_normal(ray.origin + depth*ray.direction, ray.origin));
}

Light light = Light(vec3(1.0, 1.0, 1.0), normalize(vec3(1.0, 1.0, 1.0)));

vec3 render(Ray ray) {
  vec3 color = vec3(0.0);
  vec3 color_surf = vec3(1.0, 2.0, 1.0);

  for (int i=0; i<RENDER_RAY_RECURSION; i++) {
    Intersect hit = trace(ray);
	vec3 reflection = reflect(ray.direction, hit.normal);
    Intersect next_hit = trace(Ray(ray.origin + ray.direction*hit.len + 1e9*light.direction, light.direction));
    if (next_hit == MISS){
      color += clamp(dot(hit.normal, light.direction), 0.1, 1.0)
                 * light.color * color_surf;
    }

    if (hit == MISS) { 
      color *= RENDER_AMBIENT;
      break;
    }

    ray = Ray(ray.origin + ray.direction*hit.len + RENDER_EPSILON*reflection,
      reflection);
  }
  return color;
}

/* MAIN ***********************************************************************/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  // coordinate whose (0,0) is the center of canvas.
  // independent of aspect ratio
  vec2 p = vec2((-0.5+uv.x)*(iResolution.x/iResolution.y), -0.5+uv.y);

    //Camera code from iq (https://www.shadertoy.com/view/lsfXWH)
    // camera
    float an = 10.0*iMouse.x/iResolution.x;
    vec3  ro = vec3(12.0*sin(an),0.0,12.0*cos(an));
    vec3  ta = vec3(0.0,0.0,0.0);

    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

  light.direction = normalize(vec3(1, 2.0*sin(iTime), 1.0*sin(2.0*iTime)));
  //Ray ray = Ray(vec3(0.0, 0.0, 12), normalize(vec3(p.x, p.y, -1.0)));
    Ray ray = Ray(ro, rd);
  fragColor = vec4(pow(render(ray)*RENDER_EXPOSURE, vec3(1.0/RENDER_GAMMA)), 1.0);
}
