// Wave Coefficients
float k = 4.;
float w = 5.;
float A = 0.3;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (x: from -pi to pi, y: from -1 to 1)
    float pi = acos(-1.);
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.xy*vec2(pi,1);

    //Plot of the Wave
    float dWave = A*sin(w*iTime-k*uv.x)-uv.y;
    bool thisPixelIsColored = abs(dWave)<0.03;

    // Output to screen
    fragColor = vec4(0,vec2(thisPixelIsColored),1.0);
}
