void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,1,2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI2 2.0*3.141592653589793


vec2 mainSound( in int samp, float time )
{
    float a = sin( 100. * PI2 * time);
    float b = sin( 500. * PI2 * time);
//    float s = (a + b) * 0.5;

    float t = mod( time, 10.0 ) / 10.0;
    float pitch = mix( 2300.0, 230.0, t );
    return vec2( 0.5*sin( pitch * PI2 * time ), 0.0 );
}
