float rnd(float i){
    float i1=floor(i*100.);
    float i2=ceil(i*100.);
    float i11=mod(cos(i1*mod(i1,3.)*.00632)*346.9,1.);
    float i22=mod(cos(i2*mod(i2,3.)*.00632)*346.9,1.);
    float span=mod(i*100.,1.);
    return i22*span+i11*(1.-span);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.yy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);

    float layer=floor(iTime/2.);
    float zoom=mod(iTime/2.,1.);
    float a;
    for(a=0.;a<10.;a++){
        float zooma=pow(1.2,a+zoom);
        vec2 uvzoom=uv/zooma;
        float h=rnd(a-layer+uvzoom.x)*.1-.2;
        if(h>uvzoom.y){
            fragColor = vec4(vec3(a+zoom)*.1,1.0);
        }
    }


}
