#define rotate(a)  mat2(cos(a), -sin(a), sin(a), cos(a))

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -0.5 * iResolution.xy) / iResolution.x * rotate(iTime / 3.0);

    float left = step(-0.2, uv.x); 
    float bottom = step(-0.2, uv.y);
    float top = step(0.8, 1.0 - uv.x);
    float right = step(0.8, 1.0 - uv.y);   

    vec3 color = vec3(left * top * right * bottom);

    fragColor = vec4(color, 1.0);
}
