float rand(float n){return fract(sin(n) * 43758.5453123);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 noise = vec3 (rand(uv.x*uv.y));
    vec3 c= vec3 (1.0);
    c*= step(0.35, max(fract(9.0*uv.x - 0.4*mod(floor(8.0*uv.y), 2.0)) - 0.5,fract(8.0*uv.y) - 0.5));
    if (c==vec3(0.0)){
        fragColor = vec4 (vec3 (1.0,0.0,0.0),1.0)+vec4(noise,1.0);
    } else {   
		fragColor = vec4(vec3 (0.8),1.0)+vec4(noise,1.0);
	} 
}
