void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(cos(max(length(fragCoord.xy - iMouse.xy), 7.0)*(0.8+0.2*sin(2.0*iTime+0.003*pow(length(fragCoord.xy - iMouse.xy), 0.1)))));
    fragColor *= 0.3;
    fragColor.r *= min(1.0, length(fragCoord.xy - iMouse.xy)/60.0);
    fragColor.gb *= 1.0+3.0*(1.0-min(1.0, length(fragCoord.xy - iMouse.xy)/60.0));
}
