void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float time = iTime / 1.0;

//    float amp = 0.05; // Low amp is a subtle rubber stretching

    float amp = 1.0;


    vec2 p0 = vec2(0.0, 0.0) + amp * 0.55 * vec2(sin(time*1.1), -sin(time*0.9));
    vec2 p1 = vec2(1.0, 0.0) + amp * 0.4 * vec2(-sin(time*1.2), sin(time*0.8));
    vec2 p2 = vec2(0.0, 1.0) + amp * 0.6 * vec2(sin(time*1.3), -sin(time*1.7));
    vec2 p3 = vec2(1.0, 1.0) + amp * 0.7 * vec2(-sin(time*0.8), sin(time*0.7));

    vec2 pd0 = mix(p0, p2, uv.y);
    vec2 pd1 = mix(p1, p3, uv.y);

    vec2 p = mix(pd0, pd1, uv.x);

	fragColor =  texture(iChannel0, p);


}
