vec2 complexMult(in vec2 a, in vec2 b)
{
    return vec2((a.x*b.x - a.y*b.y) + (a.x*b.y - a.y*b.x));
}

void iteration(in vec2 c, inout vec2 z)
{
    z = complexMult(z, z) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec2 target = iMouse.xy / iResolution.xy * vec2(-1.0, 1.0);
    target.x *= iResolution.x / iResolution.y;
    target = vec2(-0.088,0.654);
    float scale = pow(1.5, iTime) / 10.0;

	float x0 = (uv.x - 0.5)/scale + target.x/iTime;
    float y0 = (uv.y - 0.5)/scale + target.y/iTime;
    float x = 0.0;
    float y = 0.0;



    int i = 0;
    while(x*x + y*y < 4.0 && i < 1000)
    {
    	float xtemp = x*x - y*y + x0;
        y = 2.0*x*y + y0;
        x = xtemp;
        i += 1;
    }

    float c = 1.0/float(i+100)*100.0;

	fragColor = vec4(c, 0.0, 0.0, 1.0);
}
