void mainImage( out vec4 c,  vec2 i )
{
  	vec2 uv = i.xy / iResolution.xy;
    vec4 mus ;




  if (uv.x>.995)

    { 

   	float zoom=12.;


    mus= 0.8*texture(iChannel0,uv.yy/zoom).rrrr+ 0.8*texture(iChannel0,uv.yy+vec2(0.)/zoom).rrrr;//+float(fract(uv.y*15.+.5)<.05);


    float maxread=0.0;float mx=0.;
      for (float x=.5;x<11.;x++)

    {float xx=x/11.;
     if (texture(iChannel0,vec2(xx)).r>maxread)
     {mx=xx;maxread=texture(iChannel0,vec2(xx)).r;}

     mus+=.003*texture(iChannel0,vec2(xx)).r/distance(xx,uv.y);
     //mus+=0.0001/distance(mx,uv.y);
    }}

    else {mus=texture(iChannel1,uv+vec2(0.006,-.005+uv.y*.01));}

	c =mus;  
}

