#define h(q,s) min((q-s)/.01,.9)+max((q-s+.01)/-.01,0.)

void mainImage(out vec4 o,vec2 c)
{
    vec2 q=c/iResolution.x*2.-1.;o=vec4(1);
    for(float i=0.,s=.3;i++<8.;s*=.8)q=abs(q),q-=s*2.,
        o*=h(length(q)-.1*sin(iTime),s),o.y=pow(o.y,.9);

}


