#define seed 187

vec2 foo(vec2 src, vec2 normal)
{
	if (dot(src,normal)<.0)
	{
		src = reflect(src,normal);
	}
	return src;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy -.5;
	uv.x = uv.x*iResolution.x/iResolution.y;
	vec2 ouv=uv;
	uv*=0.44;



	float t = iTime*.25;
	t+=float(seed);

	float ang = t*.02+sin(t*.02);;
	float scale = 1.0+cos(t*.02)*0.25;
	uv *= mat2(sin(ang),cos(ang),-cos(ang),sin(ang))*scale;
	uv += vec2(sin(t*.005),cos(t*.004))*0.1;

	vec3 a = vec3(.0);

	for (float i = .0; i<1.0; i+=.01)
	{
		float fi = i+t*.0000001;
		float qq = sin(t*.003)*2.0;
		uv = foo(
			uv,vec2(cos(fi*3.14159*50.0+qq),sin(fi*3.14159*50.0+qq)) );
		uv += vec2(cos(fi*3.14159*1.0),sin(fi*3.14159*1.0));
		vec2 final = uv*(0.0+pow(60.0,i))+vec2(iTime*.01,iTime*.01);
		a+=texture(iChannel0,final).xyz*texture(iChannel1,final*.004).xyz*2.0;
	}
	a*=.01;
	a=a-vec3(.5);
	a=a*a*a*a*5096.0;

	a+=vec3(length(ouv)*-.02+.02);
	a=mix(a,vec3(length(a)),length(a)*.5+.5);

	fragColor = vec4(pow(a,vec3(.58)),1.0);
}
