const int numColors = 32;
const int NUM_PARTICLES = 96;

float sumXY(vec2 fragCoord)
{
    float sum = 0.0;
    for(int i = 0; i<NUM_PARTICLES; i++)
    {
        vec2 coordinate = vec2((float(i)+0.5)/.3, 0.5);     
        vec4 p= texture(iChannel0,coordinate).rgba;
        float loop = float(i);

        p.x+=(iResolution.x*.3)*(1.6+cos(iTime*.31+(loop*5.53)));
        p.y+=(iResolution.y*.4)*(.4+ sin(iTime*.85+(loop*3.37)));  	
        p.y+=iResolution.y*.3;

        sum += p.a * p.z/distance(p.xy, fragCoord.xy);
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float s = sumXY(fragCoord);

    uv *= .001 - .03 *sin(iTime/3.8);
    uv.x *= .001 - .04*cos(iTime/3.3);
    uv.y *= .003 - .05*sin(iTime/2.2);
    vec4 offscreen_color = texture (iChannel1, uv).rgba;
    s *= smoothstep(0.0, 1.0, offscreen_color.r);
    int colorSelect = int((float(numColors)-1.0) * clamp(s,0.0,1.0));
    vec2 coordinate = vec2((float(colorSelect)+0.5)/float(numColors),0.5); 
    coordinate.xy += .6*sin(iTime/500.);
    fragColor = texture(iChannel2,coordinate).rgba;
}
