void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 0.01;
	vec2 uv = zoom * (fragCoord.xy - iResolution.xy / 2.0);

    float t = iTime + 0.;

    float x = uv.x;
    float y = uv.y;

    float m = 5.;
    float n = 5. + 4. * sin(t/10.5); // m * (1. + t*t*t / 2.);

    float a = t/4.;
    float b = a + sin(t/1.) * 0.90;

    float value = (
       a * sin(m*x) * sin(n*y) + b * sin(n*x) * sin(m*y)
    );


    float color = sin(value) * 3.0;

    if(color > 0.0) {
      fragColor = (color - 1.) * vec4(0,0.5,1,0); 
    } else {
      fragColor = (-color - 1.) * vec4(1,0.5,0,0);
    }

}
