// This takes a little while to evolve, give it 20 seconds or so
void mainImage(out vec4 c, vec2 u)
{
    c = .5+texelFetch(iChannel0, ivec2(u),0);
}

//This borrows conceptually from victor_shepardson's shader here: https://www.shadertoy.com/view/4dBBWR
#define T(x) texture(iChannel0, fract((U x)/iResolution.xy))
void mainImage(out vec4 O, vec2 U)
{
    O=T()+sin(U.xyyy/50.)/iTime;
    for(int i = 0;i<9;i++)
        O+=T(-O.yx)-1.1*T(+O.yx);
	O = normalize(.8*T(+O.xy)+.02*O.wxyz);
}
