void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 c = texture(iChannel0, fragCoord.xy/iResolution.xy).rgb;
	fragColor = vec4(c,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 p = texture(iChannel0, fragCoord).rgb;
    vec3 c = vec3(0);
    float _y = floor(iTime*.005)/256.;
    c.r += texture(iChannel1, vec2(iTime*.005, _y)).r;
    c.g += texture(iChannel1, vec2(iTime*.005, _y)).g;
    c = mix(p, c, .05);
    c.b = length(c-p);
    fragColor = vec4(c,1.0);
}

#define UV fragCoord.xy / iResolution.xy
#define AR vec2(iResolution.x/iResolution.y,1.)
#define U (UV*2.-1.)*AR
#define PI 3.1415

float nsin(float x) {
	return sin(x)*.5+.5;
}

vec3 decay(vec3 c) {
	return vec3(pow(c.r, 1.)*.999, pow(c.g, 1.5)*.999,pow(c.b, 1.8)*.997);
}

float circle(vec2 p, float r) {
	return smoothstep(1., 1.01, 1.-length(p)+r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 self = texture(iChannel1, fragCoord.xy/iResolution.xy).rgb;
    vec3 _sample = texture(iChannel0, vec2(0.)).rgb;
    vec2 p = _sample.rg*2.-1.;
    p *= AR;
    vec3 c = vec3(0.);
    c += circle(U-p, _sample.b+.01);
    for(int i = 0; i < 4; i++) {
        vec3 off = texture(iChannel2, vec2(iTime*.01+float(i)/4.)).rgb;
        vec2 off_pos = (off.rg*2.-1.)*AR;
        c += circle(U-p+off_pos*.1, _sample.b+.01)*off;
    }
    c = c + decay(self);
	fragColor = vec4(c,1.0);
}
