void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uvDown = (fragCoord.xy+vec2(0.0,-1.0)) / iResolution.xy;
    vec2 uvUp = (fragCoord.xy+vec2(0.0,1.0)) / iResolution.xy;
    vec2 uvRight = (fragCoord.xy+vec2(1.0,0.0)) / iResolution.xy;
    vec2 uvLeft = (fragCoord.xy+vec2(-1.0,0.0)) / iResolution.xy;    

    bool cb = bool(mod(floor(fragCoord.x*0.5) + floor(fragCoord.y*0.5),2.0));


    cb = mod(float(iFrame), 2.0)==1.0?cb:!cb;
    //cb = !cb; for debugging

    if(cb)
    {
        fragColor = texture(iChannel0,uv);
    }
    else
    {
        vec2 cbCoord = mod(fragCoord.xy, vec2(2.0));
        if(cbCoord == vec2(0.5,0.5))
        {
            fragColor = 0.5*(texture(iChannel0,uvDown)+texture(iChannel0,uvLeft));
        }
        if(cbCoord == vec2(1.5,0.5))
        {
            fragColor = 0.5*(texture(iChannel0,uvDown)+texture(iChannel0,uvRight));
        }
        if(cbCoord == vec2(0.5,1.5))
        {
            fragColor = 0.5*(texture(iChannel0,uvUp)+texture(iChannel0,uvLeft));
        }
        if(cbCoord == vec2(1.5,1.5))
        {
            fragColor = 0.5*(texture(iChannel0,uvUp)+texture(iChannel0,uvRight));
        }   
    }
}
