// see also https://www.shadertoy.com/view/4dSyDR
// blue noise version of https://www.shadertoy.com/view/Md2czm

vec4 char(vec2 p, int c) {  // access to the image of ascii code c. from https://www.shadertoy.com/view/llySRh

    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel0, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx(p/16.),dFdy(p/16.) );
}

vec4 pInt(vec2 p, float n) { // display int4
    vec4 v = vec4(0,0,0,1e5), t;
    if (n < 0.) {
        t = char(p - vec2(-.5,0), 45 ); if (t.a<=1.) v.a=0., v += t;
        n = -n;
    }

    for (float i = 3.; i>=0.; i--) {
        n /= 10.;
        t = char(p - vec2(.5*i,0), 48+ int(fract(n)*10.) ); if (t.a<=1.) v.a=0., v += t;
    }
    return v;
}

float hash( uint n ) { // from https://www.shadertoy.com/view/llGSzw  Base: Hugo Elias. ToFloat: https://iquilezles.org/articles/sfrand
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.;
}

#define keyClick(ascii)  (texture(iChannel2, vec2((.5+ascii)/256.,.25)).x > 0.)


vec4 noise(vec2 U) {
    U-=.5; return vec4( hash(uint(U.x+iResolution.x*U.y)) );
}

vec4 bluenoise(vec2 U) {
#define V(i,j)  noise(U+vec2(i,j))
  //U=floor(U/8.); 
    vec4 N = 8./9.* noise( U ) 
           - 1./9.*( V(-1,-1)+V(0,-1)+V(1,-1) +V(-1,0)+V(1,0) +V(-1,1)+V(0,1)+V(1,1) );  
    return N*2. + .5;   // or *1 to avoid saturation at the price of low contrast
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 M = iMouse.xy; if (length(M)<10.) M = vec2(50);
    float a = M.y/iResolution.y;


    O = mix( bluenoise( mod(U,M.x) )-.5, // mix tiled noise...
             bluenoise(U+.5)-.5,         // and flat noise...
             a )                         // according to Mouse.y .
        / sqrt(a*a+(1.-a)*(1.-a))        // Normalization preserves contrast.
        +.5;

    O = sqrt(O);                         // gamma conversion

    vec4 V = pInt ( ( U/iResolution.y - vec2(1.,.85) ) * 10., M.x); // print scale
    if (V.a<1.) O = V.x*vec4(1,.6,0,0);
    V = pInt ( ( U/iResolution.y - vec2(1.,.75) ) * 10., a*100.);   // print correlation
    if (V.a<1.) O = V.x*vec4(1,.6,0,0);

    U = mod(U,M.x);                      // show tiles
    if (keyClick(32.) && min(U.x,U.y)==.5 ) O=vec4(1,0,0,0);
}
