//Ethan Alexander Shulman 2017


#define POINT_INTENSITY 0.1
#define ITERATIONS 1
#define FRAMES 256

#define NUM_POINTS ITERATIONS*FRAMES



//function outputs point position, radius and color from point id i
void f(int i, out vec2 p, out float r, out vec3 c) {
    float fi = (float(i)/float(NUM_POINTS))*10.,
          turn = floor(fi);
    fi = fract(fi)*6.28;
    p = (vec2(sin(fi),cos(fi))*max(0., 1.-turn/11.))*0.5+0.5;
    c = vec3(sin(fi),cos(fi),-cos(fi))*0.5+0.5;
    r = 0.1;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 60) {
        //init
        fragColor = vec4(0.);
        return;
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec4 c = texture(iChannel0, uv);
    if (iFrame > FRAMES+60) {
     	//finished
        fragColor = c;
        return;
    }


    for (int i = 0; i < ITERATIONS; i++) {
        vec2 p;
        vec3 l;
        float r;
        f(iFrame*ITERATIONS+i, p, r, l);
        c.xyz += l*POINT_INTENSITY*max(0.,1.-length(uv-p)/r);
    }

    fragColor = c;
}
