#define	R		0.25
//#define	R2		(R*R)
#define CENTER	vec2(0.5, 0.5)
#define PI		3.1415936

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float xonyratio = iResolution.x / iResolution.y;
    float timepulse = sin(((iTime-float(int(iTime / 4.0)*4))/4.0)*2.0*PI);
    float timepulse2 = sin(((iTime-float(int(iTime / 8.0)*8))/8.0)*2.0*PI);

    vec2 center = CENTER;
    // moving center over time
    center.x = center.x + timepulse*0.1;
    center.y = center.y - timepulse2*0.1;
    //float module = (uv.x-center.x)*(uv.x-center.x)*xonyratio*xonyratio + (uv.y-center.y)*(uv.y-center.y);
    vec2 uvratio = (uv - center); uvratio.x = uvratio.x * xonyratio;
    float module = length(uvratio);

    // radius pulsing over time
    //float pulsedRadius = R2*(0.75+0.25*timepulse);
    float pulsedRadius = R*(0.75+0.25*timepulse);

    if (module > pulsedRadius)
    {
        //float textureCoord = iChannel0.x;
        vec4 textureColor = texture(iChannel0,uv);
        fragColor = textureColor*0.1 + vec4(uv,0.5+0.5*sin(iTime),1.0)/(1.5+1.0*(1.0+cos(iTime)))*0.9;
    }
    else
    {
        vec4 textureColor = texture(iChannel1,uv);
		fragColor = textureColor*0.2 + vec4(uv,0.5+0.5*sin(iTime),1.0)*0.8;
    }


}
