vec4 circle(vec2 pos, vec2 center,float radius){
    float t = length(pos-center)-radius;
    float d = smoothstep(0.0, abs(sin(iTime))/2.0, t);  
    return vec4(d,1., 0.1,1.);  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/min(iResolution.x,iResolution.y);
	vec2 center = vec2(.5,.5);
	vec2 center2 = vec2(.7,.7);
    fragColor = circle(uv,center,0.2);
    fragColor += circle(uv,center2,0.3);
    fragColor -= texture(iChannel1,uv);
}
