void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    	
    float v = 1.0/iResolution.y;    
    fragColor = vec4(0.0);
    fragColor += texture(iChannel0, fragCoord/iResolution.xy + vec2(v,v), 0.0)   * .5;
    fragColor += texture(iChannel0, fragCoord/iResolution.xy + vec2(-v,-v), 0.0) * .5;
    fragColor += texture(iChannel0, fragCoord/iResolution.xy + vec2(-v,v), 0.0)  * .5;
    fragColor += texture(iChannel0, fragCoord/iResolution.xy + vec2(v,-v), 0.0)  * .5;    
    fragColor /= 2.0;     
    fragColor += texture(iChannel0, fragCoord/iResolution.xy, 0.0);    
    fragColor /= 2.0;        
    fragColor -= texture(iChannel0, fragCoord/iResolution.xy, 0.0);        
    fragColor *= 20.0;     
    fragColor.rgb = fragColor.rrr;     
}
