float time = iTime;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord / iResolution.xy;

    p.x += cos(p.y * 30. + p.x * 40. + time * 3.5) * 0.02;
    p.y += cos(p.y * 30. + p.x * 40. + time * 2.) * 0.02;

    vec4 tex = texture(iChannel0, p.xy);

    fragColor = tex;
}
