void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = vec2(fragCoord.xy / iResolution.xy);

    vec3 bc = vec3(1.0);
    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 aixeColor = vec3(1.0*cos(iTime), .5, .0);

    vec3 pixel = bc;

    float w = 0.002;

    for(float i = 0.01; i<= 1.0; i+=0.01){
    	if(abs(r.y - i) < w) pixel = vec3(1.0*sin(iTime), 0.1+i, 1.0-i);
        if(abs(r.x - i) < w/2.0) pixel = vec3(1.0-i, (0.1+i)*cos(iTime), 0.0);
    }

    if(abs(r.x - .5) < 0.002) pixel = aixeColor;
    if(abs(r.y - .5) < 0.004) pixel = aixeColor;    

    fragColor = vec4(pixel, 1.0);
}
