void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float kernel[9];
    kernel[0] = kernel[8] = 1.;
    kernel[1] = kernel[7] = 1.5;
    kernel[2] = kernel[6] = 2.;
    kernel[3] = kernel[5] = 2.5;
    kernel[4] = 4.;

    vec4 finalColor;
    float divisor;

    for(int i = -4; i <= 4; i++){
    	vec4 pixelColor = texture(iChannel0, (fragCoord + vec2(0, i)) / iResolution.xy );
        pixelColor *= kernel[i + 4];
        divisor += kernel[i + 4];
        finalColor += pixelColor;
    }

    fragColor = vec4(finalColor.x / divisor,
                     finalColor.y / divisor,
                     finalColor.z / divisor,
                     1.0);
}

