float NUM_STEPS = 10.0;

// From: https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized screen coords
    vec2 uv = fragCoord/iResolution.xy;

    // Partition pixel into a discrete step (rectangle)
    vec2 step = floor(uv*NUM_STEPS)/NUM_STEPS;

    // Change rate (i.e. period of cosine) is randomized from step
    float changeRate = rand(step);

    vec3 col = 0.5 + 0.5*cos(changeRate*iTime) * vec3(0,2,4);

    // Output to screen
    fragColor = vec4(col,1.0);
}
