float sphere (vec3 r, vec3 o, float radius){
    return length(o-(fract(r)*2.-1.))-radius;
}
vec4 trace (vec3 o, vec3 r, vec3 pos, float radius){
    float t = 0.0;
    vec3 p;
    for(int i = 0; i<32; ++i){
        p = o+r*t;
        float d = sphere(p, pos, radius);
        t += d*0.5;
    }
    return vec4(p, t);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;
    float angle = mouse.x*3.;
    float angy = (mouse.y-0.5);

    vec2 ray = vec2(uv.x+cos(angle), uv.y+sin(angy));//direction
    vec3 r = normalize(vec3(ray, sin(angle)));//ray
    vec3 o = vec3(sin(iTime), 0., iTime);//camera
    vec3 pos = vec3(0., 0., sin(angle));//position of sphere
    float radius = 0.5;
    vec4 t = trace(o, r, pos, radius);
    vec4 getPixel = texture(iChannel1, vec2(t.x-mouse.x, t.y)*1.0);
    float fog = 1.0 / (1.0 + t.w*t.w*0.5);//shadow at distance
    if(sphere(t.xyz, pos, radius)>0.2) {
        //getPixel = texture(iChannel1, vec2(uv.x-mouse.x, uv.y)); 
        //fog = 1.;
    }
    vec3 fc = getPixel.xyz*fog;
    fragColor = vec4(fc, 1);
}
