const float size = 8.0;

void mainImage(out vec4 color, in vec2 coord)
{
    if(iMouse.z < 0.0)
        discard;

    vec2 mA = abs(iMouse.zw), mB = iMouse.xy;

    vec2 l = mB - mA;
    float t = dot(coord - mA, l) / dot(l, l);

    bool r = distance(coord, mA) < size;
    bool g = distance(coord, mB) < size;
    bool b = distance(coord, mA + l * clamp(t, 0.0, 1.0)) < size;

    color = vec4(r, g, r ^^ g ^^ b, 1);
}
