const float pi = 3.141592653589793238;

vec3 timeRainbow(float t, float tbase)
{
    float abase = abs(7. - mod(tbase,14.))/14.;
    float again = abs(8. - mod(tbase,16.))/16.;
    float bbase = abs(9. - mod(tbase,18.))/18.;
    float bgain = abs(10. - mod(tbase,20.))/20.;
    float cbase = abs(11. - mod(tbase,22.))/22.;
    float cgain = abs(12. - mod(tbase,24.))/24.;

    float sinbased = clamp(abase+again*sin(t), 0., 1.);
    float sinbasedb = clamp(bbase+bgain*sin(t + (2. * pi / 3.)), 0., 1.);
    float sinbasedc = clamp(cbase+cgain*sin(t + (4. * pi / 3.)), 0., 1.);

    return vec3(sinbased, sinbasedb, sinbasedc);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float ix = iResolution.x;
    float y = fragCoord.y;
    float iy = iResolution.y;
    float xbase = x/ix;
    float tbase = iTime;   
    float xmult = (abs(5. - mod(tbase,10.))-2.5) * 3.;
    float tmult = 1.;
    float ybase = y/iy;
    float ymult = (abs(6. - mod(tbase,12.))-3.) * 2.5;
    float rbase = sqrt((x - .5*ix)* (x - .5*ix) + (y - .5*iy) * (y - .5*iy));
    float rmult = -.003*abs(50. - mod(tbase,100.));
	fragColor = vec4(timeRainbow(  xbase * xmult + tmult * tbase + ybase * ymult+ rbase * rmult, tbase),1. );
}
