void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    
    vec4 a =mix(vec4(1.0),vec4(0.0),(step(step(uv.y,1.0-uv.x),step(uv.x,uv.y))));
    vec4 b =mix(vec4(1.0),vec4(0.0),(step(step(uv.y,1.0-uv.x),step(uv.y,uv.x))));
    vec4 c =mix(vec4(1.0),vec4(0.0),(step(step(1.0-uv.y,uv.x),step(uv.y,uv.x))));
    vec4 d =mix(vec4(1.0),vec4(0.0),(step(step(1.0-uv.y,uv.x),step(uv.x,uv.y))));   
    float t = mod(floor(iTime),4.0);    
    fragColor = a*(1.0-abs(sign(t-0.0)))+b*(1.0-abs(sign(t-1.0)))+c*(1.0-abs(sign(t-2.0)))+d*(1.0-abs(sign(t-3.0)));
}
