const float PI = 3.141592654;
const vec4 highlight = vec4(1.0);
const vec4 shadow = vec4(0.0, 0.5, 0.5, 1.0);

// TODO: Implement with smoothstep instead of modulo

// The fract() function is often used as means to define the edges of a spiral.
// Using a variable modulo instead makes for some fun stretchiness.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	float r = length(uv);
    float spins = 10.0 + cos(iTime) * 8.0;
    float rotation = atan(uv.y, uv.x) + cos(iTime) * 3.0; 
    float splits = 1.0;
    float modulo = 1.5 + sin(iTime) * pow(r, 2.0) * 1.2;

    float factor = mod(rotation * splits * modulo / PI + r * spins, modulo);
    fragColor = vec4(mix(shadow, highlight, factor));

}
