float PI = 3.14159265359;

float RATIO_SPIN = .1;
float RATIO_SPREAD = 23.3;

float REPETITION_POWER = 35.0;

float RIPPLE_POWER = 20.0;
float RIPPLE_DISTANCE = 0.1;

float getAngle(vec2 uvCenter){
	float angle = atan(uvCenter.y,uvCenter.x )/(2.0*PI);
	if(angle <0.0) angle +=1.0;
	return angle;
}
float getDis(vec2 v){
	return sqrt(v.x* v.x + v.y*v.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 uvCenter = uv - vec2(0.5,0.5);
	float angle = getAngle(uvCenter) + iTime*RATIO_SPIN;
	float dis = getDis(uvCenter) ;

	dis *= 1.0+ sin(angle*2.0*PI*RIPPLE_POWER) *RIPPLE_DISTANCE; // distorts distance to ripple effect
	float color = sin(dis*PI*REPETITION_POWER -iTime*RATIO_SPREAD); //final color, use sin to create "repetition" pattern effect

	fragColor = vec4(color*.1, color*.1,color,1);
}
