void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 ms = iMouse.zw / iResolution.xy;
	float len = sqrt(length(uv-ms));
	if(len < 0.0)len = 0.0;
	if(len > 1.0)len = 1.0;
	if(iMouse.z>0.0 && iMouse.w>0.0) {
		fragColor = vec4(1.0-len,1.0-len,1.0-len,1.0);
	} else {
		fragColor = vec4(0.0,0.5,0.0,1.0);
	}
}
