#define PI 3.14159265359

float dist(vec2 uv) {
	return sqrt(pow(abs(uv.x-.5),2.)+ pow(abs(uv.y-.5),2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 pic = texture(iChannel0, uv);
	float sound = texture(iChannel1, vec2(1.,1.)).x;
	float donut = sin(dist(uv)*2.*PI);
	float noise = (sound+pic.r)/5.;
	float red = (donut - noise - .5)*100.;
 	fragColor = vec4(red,0.,0.,1.);
}
