void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = (fragCoord.xy - (iResolution.xy / 2.0));

    float x = uv.x;
    float y = uv.y;
    float value = (x*x-y*y);

    float t = iTime / 1000.;
    float color = sin(value * t) * 6.0;


    float low = abs(color) - 3.0;
    float med = abs(color) - 4.0;
    float high = max(0.,abs(color) - 5.0);
    if(color < 0.0) {
      fragColor = vec4(low, high, high, 1.);
    } else {
	  fragColor = vec4(high, high, low, 1.);
    }    
}
