void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float x = log(fragCoord.x + (sin(iTime) + 1.0) * 1000.0)/log(2.);
    float y = log(fragCoord.y + (sin(iTime) + 1.0) * 1000.0)/log(2.);

    float col = mod(mod(floor(x), 2.0) + mod(floor(y), 2.0), 2.0);

	fragColor = vec4(col * 0.2 + 0.8);
}
