float Waves(vec2 position, sampler2D tex){

float speed = 1000.0;



vec2 pos = -position.xy/50.0;

float noise = texture(tex,vec2(pos.x * 2.0 - 1.0 + pos.y,pos.y) - iTime / speed,3.0).x;
noise += texture(tex,vec2(pos.x,pos.y * 2.0 - 1.0 + pos.x)+iTime / speed,3.0).x;
noise += texture(tex,vec2(pos.y * 2.0 - 1.0 + pos.x,pos.x) * (pos * 2.0 - 1.0 + pos.yx)+iTime / speed,3.0).x;
noise += texture(tex,vec2(pos.y * 2.0 - 1.0 + pos.x,pos.x) * (pos.yx * 2.0 - 1.0 + pos.xy)-iTime / speed,3.0).x;
noise += texture(tex,vec2(pos.y * 2.0 - 1.0 + pos.x,pos.x) * (pos.yx * 2.0 - 1.0 + pos.yx)+iTime / speed,3.0).x;
noise += texture(tex,vec2(pos.y * 2.0 - 1.0 + pos.x,pos.x) * (pos * 2.0 - 1.0 + pos)-iTime / speed,3.0).x;

noise /= 5.2342;
noise = pow(noise,9.0);
return noise;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor.rgb += Waves(uv,iChannel1);

}
