//2017/6/29 10:38 I Graduated
#define PI 3.1415926f
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 xy = fragCoord.xy - iResolution.xy*0.5;
    //static circle
    if(abs(length(xy)-iResolution.y*0.1666)<2.)
        fragColor = vec4(0.5*sin(iTime+PI*0.5),0.,0.5,1.);
    vec2 move= iResolution.y*0.3333*vec2(sin(mod(iTime,36.0)/18.*PI),cos(mod(iTime,36.0)/18.*PI));
    if(abs(length(xy-move)-iResolution.y*0.1666)<2.)
        fragColor = vec4(0.5*sin(iTime+PI*0.5),0.,0.5,1.);
    //move circle
    float i,time=iTime;
    vec2 move2,move3;
    //Cardioid
    for(i=0.;i<mod(time,36.);i+=0.1)
    {
    move = iResolution.y*0.3333*vec2(sin(mod(i,36.0)/18.*PI),cos(mod(i,36.0)/18.*PI));
    move2= iResolution.y*0.1666*vec2(sin(PI+2.*mod(1.*i,36.0)/18.*PI),cos(PI+2.*mod(1.*i,36.0)/18.*PI));
    move3= move2+move;
    if(length(xy-move3)<3.)  fragColor =vec4(1.,0.,0.,0.);
    }
}
