vec3 toRGB(in float h, in float s, in float v) {
    float i = floor(h * 6.0);
    float f = h * 6.0 - i;
    float p = v * (1.0 - s);
    float q = v * (1.0 - f * s);
    float t = v * (1.0 - (1.0 - f) * s);

    float path = mod(i, 6.0);

    if (path == 0.0) 
        return vec3(v, t, p);
    if (path == 1.0) 
        return vec3(q, v, p);
    if (path == 2.0) 
        return vec3(p, v, t);
    if (path == 3.0) 
        return vec3(p, q, v);
    if (path == 4.0) 
        return vec3(t, p, v);
    if (path == 5.0) 
        return vec3(v, p, q);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 sound = texture(iChannel0, uv).rgb;

    float last = iResolution.x * iResolution.y / mod(iTime, sin(iTime));
    float n = fragCoord.x + fragCoord.y * iResolution.x;
    vec3 color = toRGB(fract(n / last) * 1.5, 1.0, 1.0);

	fragColor = vec4(color + sound, 1.0);
}
