#define AA 4.0



float Checkertexture(vec2 uv)
{
    float t = 0.0;

    if(mod(uv.x, 1.0)>0.5)
        t=1.0;
    if(mod(uv.y, 1.0)>0.5)
        t=1.0-t;

    return t;
}

float Triangletexture(vec2 uv)
{
    float t = 0.0;
    uv.y = uv.y * 0.866 + uv.x*0.5;

    if(mod(uv.y, 1.0)>mod(uv.x, 1.0))
        t=1.0-t;

    return t;
}

float Hexatexture(vec2 uv)
{
    float t = 1.0;

    uv.y = uv.y * 0.866 + uv.x*0.5;
    uv = mod(uv, vec2(3.0));

    if(uv.y>0.0 && uv.x > 0.0 && uv.y < uv.x+1.0 && uv.y > uv.x-1.0 && uv.y<2.0 && uv.x < 2.0)
        t=0.0;
    else if(uv.y > 1.0 && (uv.y < uv.x || uv.x < 1.0))
        t = 0.5;

    return t;
}

