{
    fragColor = vec4(0.0);
    for(float aaX=.0; aaX < AA; aaX++)
    {
    	for(float aaY=.0; aaY < AA; aaY++)
        {
            vec2 uv = (fragCoord.xy + vec2(aaX, aaY)/AA) / iResolution.y;
            uv = uv - 0.5*vec2(iResolution.x/iResolution.y, 1.0);
	//vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5);
    //uv.x *= iResolution.x/iResolution.y;

//    float R = 1.0;
//    float T = 0.0;

    float R = iMouse.x/iResolution.x * 10.0;
    float T = iMouse.y/iResolution.y * 2.0 * 3.14;

    if(any(lessThan(iMouse.xy, vec2(0.1))))
    {
        R = (0.6+0.5*cos(iTime * 0.5)) * 10.0;
        T = (0.5+0.5*sin(iTime * 0.5)) * 2.0 * 3.14;
    }


    vec2 rt = vec2(length(uv)*2.0, atan(uv.y, uv.x) + T);
    vec2 rt2 = vec2(tan(rt.x*3.14*0.5), rt.y);
    vec2 uv2 = rt2.x * vec2(cos(rt2.y), sin(rt2.y)) * R;


    //uv2 += iMouse.xy/iResolution.y*10.0;

    //float t = Checkertexture(uv2);
    //float t = Triangletexture(uv2);
    float t = Hexatexture(uv2);


    if(length(uv) > 0.5)
        t = 0.5;



			fragColor += vec4(vec3(t),1.0);
        }
    }
	fragColor = fragColor / AA / AA;
}
