#define PI 3.14159265
const float it = 100.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
	float mx = max(iResolution.x, iResolution.y);
	vec2 scrs = iResolution.xy/mx;
	vec2 uv = vec2(fragCoord.x, iResolution.y-fragCoord.y)/mx;
	vec2 m = vec2(iMouse.x/mx,scrs.y-iMouse.y/mx);

    uv-=scrs/2.0; // Place the origin at the center of the screen

    const float it = 100.0;

	float v = it+1.0;

	fragColor = vec4(vec3(0.0),1.0); // background color


	for (float i = 0.0; i < it; i++)
	{

		v--;
		if(floor(mod(uv.x*2.0*v+m.x*50.0,6.0))==1.0 && floor(mod(uv.y*2.0*v+m.y*50.0,6.0))==1.0){

			fragColor = vec4(i/it*(sin(i/5.0-time*5.0+2.0*PI/3.0)+1.0)/2.0,
					    	 i/it*(sin(i/5.0-time*5.0+4.0*PI/3.0)+1.0)/2.0,
					   		 i/it*(sin(i/5.0-time*5.0+6.0*PI/3.0)+1.0)/2.0,
					    	 1.0);
		}
	}

}
