vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0, 
                     0.0, 
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t1 = uv.x + iTime;
    float t2 = uv.y - iTime;
    float r = abs(sin(t1 * 0.332) + sin(t2 * 2.21)) / 2.0;
    float g = abs(sin(t2 * 0.773) + sin(t1 * 3.53)) / 2.0;
    float b = abs(sin(t1 * 0.817) + sin(t2 * 4.71)) / 2.0;
	fragColor = vec4(hsb2rgb(vec3(r, g, b)), 1.0);
}
