vec4 wood(float x){
    vec3 color = vec3(0.439, 0.200, 0.);
    float m =mod(x,3.5);
    float wood = fract(pow(m-1.8,2.))+0.26;
    return m<0.1?vec4(0.03):vec4(color-vec3(0.157, 0.102, 0.075)*wood,1.);
}
float desert(vec2 p){
    return sin(p.x)/11.+sin(p.x+p.y*0.5)/5.+sin(p.y)/13.;
}
void mainImage( out vec4 o, in vec2 p )
{
	p=p/iResolution.xy*10.+iMouse.xy/5.;
    p.x+=iTime/2.;
    o=wood(p.y+desert(p));
}
